import { View, Text,StyleSheet, FlatList } from 'react-native'
import React from 'react'

export default function FlatDemo() {
   //key value must be string here
    const names =[
        {key:1,name:"Ankit"},
        {key:2,name:"Messi"},
        {key:3,name:"Ronaldo"},
        {key:4,name:"Dybala"},
    ];


  return (
   <FlatList 
   style= {styles.listStyle}
   keyExtractor={(index)=> {  //external key extractor
           return index.key;
}}
horizontal   //for horizontally arranging lists 
    data = {names}
           renderItem={(element)=>{    //mapping or rendering item 
            console.log(element.item.name) 
            return (
              <Text style={styles.textStyle}
              > {element.item.name}</Text>
              )
           }}   
   />
  )
}

const styles= StyleSheet.create({
    textStyle:{
        fontSize: 20,
        backgroundColor:"yellow",
        margin:20
    },
    itemStyle:{
     textAlign:"center",
     margin:20,
     padding:10
    }

});
